mapper.select:
select cus.id, cus.name, u.name as owner, cus.phone, t.expectedDate as banquetDate, t.name as banquetVenue from tbl_customer cus join tbl_user u on cus.owner=u.id left join tbl_tran t on t.customerId=cus.id
cus.name like '%' #{name} '%'
and u.name like '%' #{owner} '%'
and cus.phone=#{phone}
and cus.banquetDate > #{startDate}
and cus.banquetDate < #{endDate}
order by cus.createTime desc limit #{skipCount},#{pageSize}

select count(*) from tbl_customer cus join
tbl_user u on cus.owner=u.id


        cus.name like '%' #{name} '%'


        and u.name like '%' #{owner} '%'


        and cus.phone=#{phone}


        and cus.banquetDate > #{startDate}


        and cus.banquetDate < #{endDate}
